import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import axios from "axios";
import UserList from "./components/UserList";
import AddUser from "./components/AddUser";
import EditUser from "./components/EditUser";
import "./styles.css";

const API_URL = "https://jsonplaceholder.typicode.com/users";

function App() {
  const [users, setUsers] = useState([]);
  const [currentUser, setCurrentUser] = useState(null);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await axios.get(API_URL);
      setUsers(response.data);
    } catch (error) {
      alert("Error fetching users");
    }
  };

  const addUser = async (user) => {
    try {
      await axios.post(API_URL, user);
      fetchUsers();
    } catch (error) {
      alert("Error adding user");
    }
  };

  const editUser = async (user) => {
    try {
      await axios.put(`${API_URL}/${user.id}`, user);
      fetchUsers();
      setCurrentUser(null);
    } catch (error) {
      alert("Error editing user");
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`${API_URL}/${id}`);
      fetchUsers();
    } catch (error) {
      alert("Error deleting user");
    }
  };

  return (
    <Router>
      <div className="container">
        <h1>User Management Dashboard</h1>
        <nav>
          <Link to="/add-user">Add User</Link> | <Link to="/view-users">View Users</Link>
        </nav>
        <Routes>
          <Route path="/add-user" element={<AddUser onAdd={addUser} />} />
          <Route
            path="/view-users"
            element={<UserList users={users} onEdit={setCurrentUser} onDelete={deleteUser} />}
          />
          <Route
            path="/edit-user"
            element={<EditUser user={currentUser} onSave={editUser} />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
