import React, { useState } from "react";

const AddUser = ({ onAdd }) => {
  const [user, setUser] = useState({
    id: "",
    name: "",
    username: "",
    email: "",
    department: "", // Ensure department is included in the state
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({
      ...prevUser,
      [name]: value, // Dynamically update the field based on the input name
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!user.name || !user.email || !user.department) {
      alert("Please fill out all fields.");
      return;
    }
    onAdd(user); // Call the parent component's onAdd function
    setUser({
      id: "",
      name: "",
      username: "",
      email: "",
      department: "", // Reset form after submission
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add User</h2>
      <div>
        <label>ID:</label>
        <input
          type="text"
          name="id"
          value={user.id}
          onChange={handleChange}
          placeholder="Enter ID"
        />
      </div>
      <div>
        <label>First Name:</label>
        <input
          type="text"
          name="name"
          value={user.name}
          onChange={handleChange}
          placeholder="Enter First Name"
        />
      </div>
      <div>
        <label>Last Name:</label>
        <input
          type="text"
          name="username"
          value={user.username}
          onChange={handleChange}
          placeholder="Enter Last Name"
        />
      </div>
      <div>
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={user.email}
          onChange={handleChange}
          placeholder="Enter Email"
        />
      </div>
      <div>
        <label>Department:</label>
        <input
          type="text"
          name="department"
          value={user.department}
          onChange={handleChange} // Ensure this handler is connected
          placeholder="Enter Department"
        />
      </div>
      <button type="submit">Add User</button>
    </form>
  );
};

export default AddUser;
