import React, { useState, useEffect } from "react";
import AddUser from "./components/AddUser";
import UserList from "./components/UserList";
import Pagination from "./components/Pagination";

function App() {
  const [users, setUsers] = useState([]);
  const [isAddUser, setIsAddUser] = useState(false);

  useEffect(() => {
    // Fetch initial users (from a mock API like JSONPlaceholder)
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    // Simulating fetch call for demonstration. Replace with real API request if needed
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();
    // Add a dummy department field to each user for demonstration purposes
    const usersWithDepartment = data.map((user) => ({
      ...user,
      department: "Engineering", // Default department
    }));
    setUsers(usersWithDepartment);
  };

  const handleAddUser = (newUser) => {
    setUsers((prevUsers) => [...prevUsers, newUser]);
    setIsAddUser(false); // Close the AddUser form after adding
  };

  const handleEditUser = (updatedUser) => {
    setUsers((prevUsers) =>
      prevUsers.map((user) =>
        user.id === updatedUser.id ? updatedUser : user
      )
    );
  };

  const handleDeleteUser = (userId) => {
    setUsers((prevUsers) => prevUsers.filter((user) => user.id !== userId));
  };

  const handlePageChange = (pageNumber) => {
    console.log(`Page changed to: ${pageNumber}`);
    // Handle page change if required (e.g., fetch users for that page)
  };

  return (
    <div>
      <h1>User Management Dashboard</h1>

      <button onClick={() => setIsAddUser(!isAddUser)}>
        {isAddUser ? "Cancel Add User" : "Add User"}
      </button>

      {isAddUser ? (
        <AddUser onAddUser={handleAddUser} />
      ) : (
        <UserList
          users={users}
          onEdit={handleEditUser}
          onDelete={handleDeleteUser}
        />
      )}

      <Pagination
        users={users}
        usersPerPage={10}
        onPageChange={handlePageChange}
      />
    </div>
  );
}

export default App;
