import React, { useState, useEffect } from "react";
import './AddUser.css';  // Import the CSS file for AddUser component

function AddUser({ onAddOrEdit, editUser }) {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [department, setDepartment] = useState("");

  useEffect(() => {
    if (editUser) {
      setFirstName(editUser.firstName);
      setLastName(editUser.lastName);
      setEmail(editUser.email);
      setDepartment(editUser.department);
    }
  }, [editUser]);

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!firstName || !lastName || !email || !department) {
      alert("Please fill in all fields.");
      return;
    }

    const newUser = {
      id: editUser ? editUser.id : Date.now(), // If editing, retain the same ID, else generate a new one
      firstName,
      lastName,
      email,
      department,
    };

    // Call the onAddOrEdit function passed from the parent
    onAddOrEdit(newUser);

    // Show success alert
    if (editUser) {
      alert(`User details updated successfully!`);
    } else {
      alert(`User added successfully! User ID: ${newUser.id}`);
    }

    // Reset form fields after submit
    setFirstName("");
    setLastName("");
    setEmail("");
    setDepartment("");
  };

  return (
    <div className="add-user-container">
      <form onSubmit={handleSubmit} className="user-form">
        <h2>{editUser ? "Edit User" : "Add New User"}</h2>

        <div>
          <label htmlFor="firstName">First Name</label>
          <input
            type="text"
            id="firstName"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="lastName">Last Name</label>
          <input
            type="text"
            id="lastName"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        <div>
          <label htmlFor="department">Department</label>
          <input
            type="text"
            id="department"
            value={department}
            onChange={(e) => setDepartment(e.target.value)}
          />
        </div>

        <button type="submit">{editUser ? "Save Changes" : "Add User"}</button>
      </form>
    </div>
  );
}

export default AddUser;
