import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import AddUser from "./components/AddUser";
import UserList from "./components/UserList";


const App = () => {
  const [users, setUsers] = useState([]); // Store the list of users
  const [currentUser, setCurrentUser] = useState(null); // Store the user being edited

  // Function to handle adding or editing a user
  const handleAddOrEditUser = (user) => {
    if (user.id) {
      // Update an existing user
      setUsers((prevUsers) =>
        prevUsers.map((u) => (u.id === user.id ? user : u))
      );
    } else {
      // Add a new user
      const newUser = { ...user, id: Date.now().toString() }; // Assign a unique ID
      setUsers((prevUsers) => [...prevUsers, newUser]);
    }
    setCurrentUser(null); // Reset the editing state
  };

  // Function to handle deleting a user
  const handleDeleteUser = (id) => {
    setUsers((prevUsers) => prevUsers.filter((user) => user.id !== id));
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/add-user">Add User</Link>
            </li>
            <li>
              <Link to="/view-users">View Users</Link>
            </li>
          </ul>
        </nav>

        <Routes>
          {/* Route for adding or editing a user */}
          <Route
            path="/add-user"
            element={
              <AddUser
                onAddOrEdit={handleAddOrEditUser}
                userToEdit={currentUser} // Pass the user to edit
              />
            }
          />

          {/* Route for viewing users */}
          <Route
            path="/view-users"
            element={
              <UserList
                users={users}
                onEdit={(user) => setCurrentUser(user)} // Set the user to edit
                onDelete={handleDeleteUser} // Handle user deletion
              />
            }
          />

          {/* Default Route */}
          <Route path="*" element={<UserList users={users} />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
