import React, { useState, useEffect } from "react";
import AddUser from "./components/AddUser";
import UserList from "./components/UserList";
import Pagination from "./components/Pagination";

function App() {
  const [users, setUsers] = useState([]);
  const [isAddUser, setIsAddUser] = useState(false);
  const [editUser, setEditUser] = useState(null); // Track which user to edit
  const [currentPage, setCurrentPage] = useState(1); // Track current page
  const [usersPerPage, setUsersPerPage] = useState(10); // Number of users per page (default to 10)
  const [searchQuery, setSearchQuery] = useState(""); // Track search query
  const [logs, setLogs] = useState([]); // Track user activity logs

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();

    // Map the data to split the name into firstName and lastName
    const usersWithFirstLastNames = data.map((user) => {
      const [firstName, lastName] = user.name.split(" ");
      return {
        ...user,
        firstName,
        lastName,
        department: user.company.name, // Using company name for department
      };
    });

    setUsers(usersWithFirstLastNames);
  };

  const handleAddUser = (newUser) => {
    setUsers((prevUsers) => [...prevUsers, newUser]);
    setIsAddUser(false); // Close the AddUser form after adding

    // Add log for user addition
    setLogs((prevLogs) => [
      ...prevLogs,
      `User ID: ${newUser.id} added successfully`,
    ]);

    // Alert message after adding a user
    alert(`User added successfully with ID: ${newUser.id}`);
  };

  const handleEditUser = (updatedUser) => {
    setUsers((prevUsers) =>
      prevUsers.map((user) => (user.id === updatedUser.id ? updatedUser : user))
    );
    setEditUser(null); // Clear edit user after saving
    setIsAddUser(false); // Close the form after editing

    // Add log for user editing
    setLogs((prevLogs) => [
      ...prevLogs,
      `User ID: ${updatedUser.id} edited successfully`,
    ]);

    // Alert message after updating a user
    alert(`User details updated successfully for ID: ${updatedUser.id}`);
  };

  const handleDeleteUser = (userId) => {
    setUsers((prevUsers) => prevUsers.filter((user) => user.id !== userId));

    // Add log for user deletion
    setLogs((prevLogs) => [...prevLogs, `User ID: ${userId} deleted successfully`]);

    // Alert message after deleting a user
    alert(`User with ID: ${userId} deleted successfully`);
  };

  // Search logic
  const filteredUsers = users.filter(
    (user) =>
      user.firstName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.lastName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Calculate the current users to display based on the current page and users per page
  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

  // Handle page change
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Handle change in users per page
  const handleUsersPerPageChange = (e) => {
    setUsersPerPage(Number(e.target.value)); // Update usersPerPage
    setCurrentPage(1); // Reset to the first page when changing the number of users per page
  };

  return (
    <div>
      <h1>User Management Dashboard</h1>

      {/* Search Input */}
      <input
        type="text"
        placeholder="Search by name or email"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        style={{
          marginBottom: "1rem",
          padding: "0.5rem",
          fontSize: "1rem",
          width: "100%",
        }}
      />

      <button onClick={() => setIsAddUser(!isAddUser)}>
        {isAddUser ? "Cancel Add User" : "Add User"}
      </button>

      {/* Dropdown for Users Per Page */}
      <div style={{ margin: "1rem 0" }}>
        <label htmlFor="usersPerPage" style={{ marginRight: "0.5rem" }}>
          Users Per Page:
        </label>
        <select
          id="usersPerPage"
          value={usersPerPage}
          onChange={handleUsersPerPageChange}
          style={{ padding: "0.5rem", fontSize: "1rem" }}
        >
          <option value={5}>5</option>
          <option value={10}>10</option>
          <option value={15}>15</option>
          <option value={20}>20</option>
        </select>
      </div>

      {isAddUser ? (
        <AddUser
          onAddOrEdit={editUser ? handleEditUser : handleAddUser} // Conditionally pass the appropriate function
          editUser={editUser} // For editing purposes
        />
      ) : (
        <>
          <UserList
            users={currentUsers} // Only display the current users for the page
            onEdit={(user) => {
              setEditUser(user); // Set the user to edit
              setIsAddUser(true); // Open the AddUser form for editing
            }}
            onDelete={handleDeleteUser}
          />
          {/* "No users found" Message */}
          {filteredUsers.length === 0 && (
            <p style={{ color: "red", marginTop: "1rem" }}>No users found</p>
          )}
        </>
      )}

      <Pagination
        totalUsers={filteredUsers.length} // Total number of users after filtering
        usersPerPage={usersPerPage} // Number of users per page
        currentPage={currentPage} // Current page number
        onPageChange={handlePageChange} // Function to handle page change
      />

      {/* Display Logs */}
      <div style={{ marginTop: "2rem" }}>
        <h3>Activity Logs:</h3>
        <ul style={{ background: "#f9f9f9", padding: "1rem", borderRadius: "8px" }}>
          {logs.length > 0 ? (
            logs.map((log, index) => <li key={index}>{log}</li>)
          ) : (
            <p>No actions logged yet</p>
          )}
        </ul>
      </div>
    </div>
  );
}

export default App;
