import React, { useState, useEffect } from "react";
import AddUser from "./components/AddUser";
import UserList from "./components/UserList";
import Pagination from "./components/Pagination";

function App() {
  const [users, setUsers] = useState([]);
  const [isAddUser, setIsAddUser] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState("");
  const [departmentFilter, setDepartmentFilter] = useState("All");
  const [activityLogs, setActivityLogs] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();

    const usersWithFirstLastNames = data.map((user) => {
      const [firstName, lastName] = user.name.split(" ");
      return {
        ...user,
        firstName,
        lastName,
        department: user.company.name,
      };
    });

    setUsers(usersWithFirstLastNames);
  };

  const handleAddUser = (newUser) => {
    setUsers((prevUsers) => [...prevUsers, newUser]);
    setActivityLogs((prevLogs) => [
      ...prevLogs,
      `User added: ${newUser.firstName} ${newUser.lastName}`,
    ]);
    setIsAddUser(false);
    alert(`User added successfully with ID: ${newUser.id}`);
  };

  const handleEditUser = (updatedUser) => {
    setUsers((prevUsers) =>
      prevUsers.map((user) => (user.id === updatedUser.id ? updatedUser : user))
    );
    setActivityLogs((prevLogs) => [
      ...prevLogs,
      `User edited: ${updatedUser.firstName} ${updatedUser.lastName}`,
    ]);
    setEditUser(null);
    setIsAddUser(false);
    alert(`User details updated successfully for ID: ${updatedUser.id}`);
  };

  const handleDeleteUser = (userId) => {
    const userToDelete = users.find((user) => user.id === userId);
    setUsers((prevUsers) => prevUsers.filter((user) => user.id !== userId));
    setActivityLogs((prevLogs) => [
      ...prevLogs,
      `User deleted: ${userToDelete.firstName} ${userToDelete.lastName}`,
    ]);
    alert(`User with ID: ${userId} deleted successfully`);
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    setCurrentPage(1); // Reset to first page when searching
  };

  const handleDepartmentFilterChange = (e) => {
    setDepartmentFilter(e.target.value);
    setCurrentPage(1); // Reset to first page when filtering
  };

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;

  const filteredUsers = users.filter((user) => {
    const matchesSearch =
      user.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment =
      departmentFilter === "All" || user.department === departmentFilter;

    return matchesSearch && matchesDepartment;
  });

  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

  return (
    <div>
      <h1>User Management Dashboard</h1>

      <div>
        <input
          type="text"
          placeholder="Search by name or email"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <select value={departmentFilter} onChange={handleDepartmentFilterChange}>
          <option value="All">All Departments</option>
          {Array.from(new Set(users.map((user) => user.department))).map(
            (department) => (
              <option key={department} value={department}>
                {department}
              </option>
            )
          )}
        </select>
        <button onClick={() => setIsAddUser(!isAddUser)}>
          {isAddUser ? "Cancel Add User" : "Add User"}
        </button>
      </div>

      {isAddUser ? (
        <AddUser
          onAddOrEdit={editUser ? handleEditUser : handleAddUser}
          editUser={editUser}
        />
      ) : (
        <UserList
          users={currentUsers}
          onEdit={(user) => {
            setEditUser(user);
            setIsAddUser(true);
          }}
          onDelete={handleDeleteUser}
        />
      )}

      <Pagination
        totalUsers={filteredUsers.length}
        usersPerPage={usersPerPage}
        currentPage={currentPage}
        onPageChange={handlePageChange}
      />

      <h2>Activity Logs</h2>
      <ul>
        {activityLogs.map((log, index) => (
          <li key={index}>{log}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
