import React, { useState, useEffect } from "react";

const AddUser = ({ onAddOrEdit, userToEdit }) => {
  const [user, setUser] = useState({
    id: "",
    name: "",
    username: "",
    email: "",
    department: "",
  });

  // Populate the form if a user is being edited
  useEffect(() => {
    if (userToEdit) {
      setUser(userToEdit);
    }
  }, [userToEdit]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!user.name || !user.email || !user.department) {
      alert("Please fill out all fields.");
      return;
    }
    onAddOrEdit(user); // Call parent function to add or edit user
    setUser({
      id: "",
      name: "",
      username: "",
      email: "",
      department: "",
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>{user.id ? "Edit User" : "Add User"}</h2>
      <div>
        <label>First Name:</label>
        <input
          type="text"
          name="name"
          value={user.name}
          onChange={handleChange}
          placeholder="Enter First Name"
        />
      </div>
      <div>
        <label>Last Name:</label>
        <input
          type="text"
          name="username"
          value={user.username}
          onChange={handleChange}
          placeholder="Enter Last Name"
        />
      </div>
      <div>
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={user.email}
          onChange={handleChange}
          placeholder="Enter Email"
        />
      </div>
      <div>
        <label>Department:</label>
        <input
          type="text"
          name="department"
          value={user.department}
          onChange={handleChange}
          placeholder="Enter Department"
        />
      </div>
      <button type="submit">{user.id ? "Update User" : "Add User"}</button>
    </form>
  );
};

export default AddUser;
