import React, { useState, useEffect } from "react";
import AddUser from "./components/AddUser";
import UserList from "./components/UserList";
import Pagination from "./components/Pagination";
import "./App.css";

function App() {
  const [users, setUsers] = useState([]);
  const [isAddUser, setIsAddUser] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage, setUsersPerPage] = useState(10);
  const [searchTerm, setSearchTerm] = useState("");
  const [activityLogs, setActivityLogs] = useState([]);
  const [showActivityLogs, setShowActivityLogs] = useState(false);
  const [nextUserId, setNextUserId] = useState(11); // Start new user IDs from 11

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    const response = await fetch("https://jsonplaceholder.typicode.com/users");
    const data = await response.json();
    const usersWithFirstLastNames = data.map((user) => {
      const [firstName, lastName] = user.name.split(" ");
      return {
        ...user,
        firstName,
        lastName,
        department: user.company.name,
      };
    });
    setUsers(usersWithFirstLastNames);

    // Ensure the nextUserId starts correctly based on existing users
    const maxId = Math.max(...data.map((user) => user.id), 10); // Default to 10 if no users
    setNextUserId(maxId + 1);
  };

  const handleAddUser = (newUser) => {
    const userWithId = { ...newUser, id: nextUserId };
    setUsers((prevUsers) => [...prevUsers, userWithId]);
    setNextUserId((prevId) => prevId + 1); // Increment next user ID
    setIsAddUser(false);
    setActivityLogs((prevLogs) => [
      ...prevLogs,
      `User ID: ${userWithId.id} added`,
    ]);
    alert(`User added successfully with ID: ${userWithId.id}`);
  };

  const handleEditUser = (updatedUser) => {
    setUsers((prevUsers) =>
      prevUsers.map((user) => (user.id === updatedUser.id ? updatedUser : user))
    );
    setEditUser(null);
    setIsAddUser(false);
    setActivityLogs((prevLogs) => [
      ...prevLogs,
      `User ID: ${updatedUser.id} edited`,
    ]);
    alert(`User details updated successfully for ID: ${updatedUser.id}`);
  };

  const handleDeleteUser = (userId) => {
    setUsers((prevUsers) => prevUsers.filter((user) => user.id !== userId));
    setActivityLogs((prevLogs) => [
      ...prevLogs,
      `User ID: ${userId} deleted`,
    ]);
    alert(`User with ID: ${userId} deleted successfully`);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value.toLowerCase());
  };

  const handleUsersPerPageChange = (e) => {
    setUsersPerPage(Number(e.target.value));
    setCurrentPage(1);
  };

  const filteredUsers = users.filter(
    (user) =>
      user.firstName.toLowerCase().includes(searchTerm) ||
      user.lastName.toLowerCase().includes(searchTerm) ||
      user.email.toLowerCase().includes(searchTerm)
  );

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = filteredUsers.slice(indexOfFirstUser, indexOfLastUser);

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div className="app-container">
      {/* Dashboard Title */}
      <h1 className="dashboard-title">User Management Dashboard</h1>

      {/* Header */}
      <div className="header">
        <div className="button-group">
          <button
            onClick={() => setIsAddUser(!isAddUser)}
            className="add-user-button"
          >
            Add User
          </button>
          {isAddUser && (
            <button
              onClick={() => setIsAddUser(false)}
              className="cancel-add-user-button"
            >
              Cancel Add User
            </button>
          )}
        </div>
        <button
          className="activity-button"
          onClick={() => setShowActivityLogs(!showActivityLogs)}
        >
          Account Activity
        </button>
      </div>

      {/* Account Activity */}
      {showActivityLogs ? (
        <div className="activity-logs">
          <h2>Activity Logs</h2>
          {activityLogs.length > 0 ? (
            <ul>
              {activityLogs.map((log, index) => (
                <li key={index}>{log}</li>
              ))}
            </ul>
          ) : (
            <p>No activity logs available.</p>
          )}
        </div>
      ) : (
        <>
          {/* Search */}
          <div className="search-container">
            <input
              type="text"
              placeholder="Search by name or email"
              value={searchTerm}
              onChange={handleSearch}
            />
          </div>

          {/* Add or Edit User */}
          {isAddUser ? (
            <AddUser
              onAddOrEdit={editUser ? handleEditUser : handleAddUser}
              editUser={editUser}
            />
          ) : (
            <UserList
              users={currentUsers}
              onEdit={(user) => {
                setEditUser(user);
                setIsAddUser(true);
              }}
              onDelete={handleDeleteUser}
            />
          )}

          {/* Pagination Settings */}
          <div className="pagination-settings">
            <label>Users per page:</label>
            <select value={usersPerPage} onChange={handleUsersPerPageChange}>
              <option value={5}>5</option>
              <option value={10}>10</option>
              <option value={15}>15</option>
              <option value={20}>20</option>
            </select>
          </div>

          {/* Pagination */}
          <Pagination
            totalUsers={filteredUsers.length}
            usersPerPage={usersPerPage}
            currentPage={currentPage}
            onPageChange={handlePageChange}
          />
        </>
      )}
    </div>
  );
}

export default App;
